mapper.insert:
insert into attachedphoto(
	attachedPhotoId, originType, uploadUserId
) values (
	#{attachedPhotoId}, #{originType}, #{uploadUserId}
)

mapper.select:
select attachedPhotoId, originType, uploadUserId, uploadedDate, isDeleted, isUnsigned, deletedDate  
from attachedphoto 
where attachedPhotoId = #{photoid}

mapper.update:
	update attachedphoto 
	   set isDeleted = #{isDeleted},
   		   deletedDate = #{deletedDate}
	 where attachedPhotoId = #{attachedPhotoId}
