mapper.insert:
insert into review(
	reviewId, placeId, userId, content, attachedPhotoCount
) values (
	#{reviewId}, #{placeId}, #{userId}, #{content}, #{attachedPhotoCount}
)

mapper.select:
select reviewId, placeId, userId, content, isDeleted, reviewedDate, modifiedDate, deletedDate, attachedPhotoCount
from review 
where reviewId = #{reviewid}

mapper.update:
	update review 
	   set content = #{content},
   		   modifiedDate = #{modifiedDate},
   		   attachedPhotoCount = #{attachedPhotoCount}
	 where reviewId = #{reviewId}
