mapper.insert:
insert into user(
	userId, nickname, password
) values (
	#{userId}, #{nickname}, #{password}
)

mapper.select:
select userId, nickname, password, registrationDate, nicknameModifiedDate, isUnsigned, unsignedDate, isRecommendated, totalPoint 
from user 
where userId = #{userid}

mapper.update:
update user 
   set nickname = #{nickname}, 
   	   totalPoint = #{totalPoint}
 where userId = #{userId}
